import { db } from '../firebase'; // Ajuste o caminho se necessário
import { collection, addDoc, getDocs, query, where } from 'firebase/firestore';

// Função para adicionar um relato
export const addRelato = async (relato) => {
    try {
        const docRef = await addDoc(collection(db, "relatos"), relato);
        console.log("Relato adicionado com ID: ", docRef.id);
    } catch (e) {
        console.error("Erro ao adicionar relato: ", e);
    }
};

// Função para obter todos os relatos
export const getRelatos = async () => {
    const querySnapshot = await getDocs(collection(db, "relatos"));
    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
};

// Função para buscar relatos em um intervalo de datas
export const buscarRelatosPorData = async (dataInicio, dataFim) => {
    const q = query(
        collection(db, "relatos"),
        where("data", ">=", dataInicio),  // Filtra relatos com data maior ou igual à dataInicio
        where("data", "<=", dataFim)      // Filtra relatos com data menor ou igual à dataFim
    );
    const querySnapshot = await getDocs(q);
    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
};
