import { db } from '../firebase';

// Função para salvar relato no Firestore
export const salvarRelato = (relato) => {
    return db.collection('relatos').add({
        ...relato,
        data: new Date() // Adiciona a data atual ao relato
    });
};

// Função para buscar relatos do Firestore
export const buscarRelatos = () => {
    return db.collection('relatos').get().then((snapshot) => {
        return snapshot.docs.map((doc) => doc.data());
    });
};

// Função para buscar relatos por data
export const buscarRelatosPorData = (dataInicial, dataFinal) => {
    return db.collection('relatos')
        .where("data", ">=", new Date(dataInicial))
        .where("data", "<=", new Date(dataFinal))
        .get()
        .then((snapshot) => {
            return snapshot.docs.map((doc) => doc.data());
        });
};
