// src/components/MapView.js
import React, { useEffect, useState } from 'react';
import { getRelatos } from '../services/firebaseService';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function MapView() {
    const [map, setMap] = useState(null);

    useEffect(() => {
        const initMap = () => {
            const mapInstance = L.map('map').setView([-15.7801, -47.9292], 4); // Centro do Brasil
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 19,
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            }).addTo(mapInstance);

            setMap(mapInstance);
        };

        initMap();
    }, []);

    useEffect(() => {
        const fetchRelatos = async () => {
            const relatos = await getRelatos();

            relatos.forEach(relato => {
                const { localizacao } = relato;
                const marker = L.marker([localizacao.latitude, localizacao.longitude]);
                marker.addTo(map).bindPopup(relato.descricao);
            });
        };

        if (map) {
            fetchRelatos();
        }
    }, [map]);

    return <div id="map"></div>;
}

export default MapView;
