// src/components/RelatoForm.js
import React, { useState } from 'react';
import { addRelato } from '../services/firebaseService';

const cidades = ['Cidade A', 'Cidade B', 'Cidade C']; // Adicione suas cidades
const bairros = ['Bairro A', 'Bairro B', 'Bairro C']; // Adicione seus bairros

function RelatoForm() {
    const [relato, setRelato] = useState({ descricao: '', localizacao: '', cidade: '', bairro: '' });
    const [erro, setErro] = useState('');
    const [sucesso, setSucesso] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        setSucesso('');

        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(async (position) => {
                const localizacao = {
                    latitude: position.coords.latitude,
                    longitude: position.coords.longitude
                };

                try {
                    await addRelato({ ...relato, localizacao });
                    setSucesso("Relato enviado com sucesso!");
                    setRelato({ descricao: '', localizacao: '', cidade: '', bairro: '' });
                } catch (e) {
                    setErro("Erro ao enviar relato. Tente novamente.");
                }
            }, () => {
                setErro("Não foi possível obter a localização.");
            });
        } else {
            setErro("Geolocalização não é suportada pelo seu navegador.");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Descrição:</label>
            <input
                type="text"
                value={relato.descricao}
                onChange={(e) => setRelato({ ...relato, descricao: e.target.value })}
                required
            />

            <label>Cidade:</label>
            <select
                value={relato.cidade}
                onChange={(e) => setRelato({ ...relato, cidade: e.target.value })}
                required
            >
                <option value="">Selecione a Cidade</option>
                {cidades.map((cidade) => (
                    <option key={cidade} value={cidade}>{cidade}</option>
                ))}
            </select>

            <label>Bairro:</label>
            <select
                value={relato.bairro}
                onChange={(e) => setRelato({ ...relato, bairro: e.target.value })}
                required
            >
                <option value="">Selecione o Bairro</option>
                {bairros.map((bairro) => (
                    <option key={bairro} value={bairro}>{bairro}</option>
                ))}
            </select>

            <button type="submit">Enviar Relato</button>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            {sucesso && <p style={{ color: 'green' }}>{sucesso}</p>}
        </form>
    );
}

export default RelatoForm;
