import React, { useEffect, useState } from 'react';
import { buscarRelatosPorData } from '../services/firebaseService';
import { Line } from 'react-chartjs-2';

function RelatoChart() {
    const [dadosGrafico, setDadosGrafico] = useState({ labels: [], datasets: [] });

    useEffect(() => {
        const fetchRelatos = async () => {
            const relatos = await buscarRelatosPorData('2024-01-01', '2024-12-31');
            const labels = relatos.map((relato) => new Date(relato.data).toLocaleDateString());
            const dataset = Array(labels.length).fill(1); // Contagem de relatos por data (1 por relato)
            
            setDadosGrafico({
                labels: labels,
                datasets: [
                    {
                        label: 'Focos de Dengue',
                        data: dataset,
                        fill: false,
                        borderColor: 'red'
                    }
                ]
            });
        };

        fetchRelatos();
    }, []);

    return (
        <div>
            <h2>Gráfico de Focos de Dengue</h2>
            <Line data={dadosGrafico} />
        </div>
    );
}

export default RelatoChart;
