import React, { useEffect, useState } from 'react';
import { getRelatos } from '../services/firebaseService'; // Corrigido para usar getRelatos
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function MapView() {
    const [map, setMap] = useState(null);

    useEffect(() => {
        const initMap = () => {
            const mapInstance = L.map('map').setView([-23.55052, -46.633308], 10);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; OpenStreetMap contributors'
            }).addTo(mapInstance);
            setMap(mapInstance);
        };

        if (!map) {
            initMap();
        } else {
            getRelatos().then((relatos) => {  // Correção aqui
                relatos.forEach((relato) => {
                    const marker = L.marker([relato.localizacao.latitude, relato.localizacao.longitude]).addTo(map);
                    marker.bindPopup(relato.descricao);
                });
            });
        }
    }, [map]);

    return <div id="map" style={{ height: '500px' }}></div>;
}

export default MapView;
