import React, { useEffect, useState } from 'react';
import { buscarRelatosPorData } from '../services/firebaseService';
import { Line } from 'react-chartjs-2';

function RelatoChart() {
    const [dadosGrafico, setDadosGrafico] = useState({ labels: [], datasets: [] });

    useEffect(() => {
        buscarRelatosPorData('2024-01-01', '2024-12-31').then((relatos) => {
            const labels = relatos.map((relato) => new Date(relato.data).toLocaleDateString());
            const dataset = relatos.map((relato) => 1); // Contagem de relatos por data
            setDadosGrafico({
                labels: labels,
                datasets: [
                    {
                        label: 'Focos de Dengue',
                        data: dataset,
                        fill: false,
                        borderColor: 'red'
                    }
                ]
            });
        });
    }, []);

    return <Line data={dadosGrafico} />;
}

export default RelatoChart;
